import { React, useEffect } from 'react';
import { useState } from 'react';
import { Line } from 'react-chartjs-2';


// Create LineChartAge that plots a line chart with age on x-axis and count on y-axis

const LineChartAge = () => {
  const [vaccinated, getVaccinated] = useState([{}]);
  const [unvaccinated, getUnvaccinated] = useState([{}]);
  useEffect(()=>{
    fetch('http://localhost:3001/counts?is_vaccinated=false')
    .then(res => res.json())
    .then(data => {
      getUnvaccinated(data);
    })
    fetch('http://localhost:3001/counts?is_vaccinated=false')
    .then(res => res.json())
    .then(data => {
      getVaccinated(data);
    })
  },[]);

  const data = {
    labels: people.map((person) => person.age),
    datasets: [
      {
        label: 'Age',
        data: unvaccinated.map((person) => person.count),
        fill: false,
        backgroundColor: 'rgb(255, 192, 192)',
        borderColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: 'Age',
        data: people.map((person) => person.count),
        fill: false,
        backgroundColor: 'rgb(255, 192, 192)',
        borderColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  return (
    <>
      <div className='header'>
        <h1 className='title'>Line Chart</h1>
      </div>
      <Line data={data} options={options} />
    </>
  );
}

export default LineChartAge;
